:0},"end":{"row":42,"column":0},"action":"insert","lines":["#!/bin/bash","# 無限ループ内でユーザーにメニューを表示し、選択肢に応じて適切な処理を行う","while true; do","    echo \"パスワードマネージャーへようこそ！\"","    echo \"次の選択肢から入力してください(Add Password/Get Password/Exit):\"","    # ユーザーからの入力を受け取り、choice変数に代入する","    read choice","","    case $choice in","        \"Add Password\")","            read -p \"サービス名を入力してください: \" serviceName","            read -p \"ユーザー名を入力してください: \" userName","            # -s: 入力されたパスワードを表示させない","            read -s -p \"パスワードを入力してください: \" password","            # 入力された情報をpasswords.txtファイルに追記する","            echo \"$serviceName:$userName:$password\" >> passwords.txt","            echo \"パスワードの追加は成功しました。\"","            ;;","        \"Get Password\")","            read -p \"サービス名を入力してください: \" serviceName","            # serviceNameに対応するpassをpasswords.txtファイルから取得","            password=$(grep \"^$serviceName:\" passwords.txt | cut -d: -f3)","","            if [ -z \"$password\" ]; then","                echo \"そのサービスは登録されていません。\"","            else","                echo \"サービス名：$serviceName\"","                # サービス名に対応するユーザー名を表示","                echo \"ユーザー名：$(grep \"^$serviceName:\" passwords.txt | cut -d: -f2)\"","                # サービス名に対応するパスワードを表示","                echo \"パスワード：$password\"","            fi","            ;;","        \"Exit\")","            echo \"Thank you!\"","            exit","            ;;","        *)","            echo \"入力が間違えています。Add Password/Get Password/Exitから入力してください。\"","            ;;","    esac","done",""]}]]},"ace":{"folds":[],"scrolltop":265.8123779296875,"scrollleft":0,"selection":{"start":{"row":42,"column":0},"end":{"row":42,"column":0},"isBackwards":false},"options":{"guessTabSize":true,"useWrapMode":false,"wrapToView":true},"firstLineState":{"row":15,"state":"start","mode":"ace/mode/sh"}},"timestamp":1698728395525,"hash":"c0a493218274576fd098ba8c66bddeffbd35a7ef"}